css:
.sheet-toolbar {
  border-bottom: 1px solid #2c2c2c;
  box-shadow: none;
}
.thread-icon:not(.thread-icon-unread):not(.thread-icon-star) {
  filter: invert(100%);
}
img.content-dark {
  filter: invert(100%);
}
img.content-light {
  filter: invert(100%);
}
.mail-label {
  filter: contrast(110%) brightness(85%);
}
#message-list .message-item-wrap .message-item-white-wrap {
  box-shadow: 0 0 0.5px rgba(255, 255, 255, 0.28), 0 1px 1.5px rgba(255, 255, 255, 0.08);
}
.thread-list .list-item:not(.unread):not(.selected):not(.focused) {
  color: #aaa;
}
.theme-picker {
  text-align: center;
  cursor: default;
}
.theme-picker h4 {
  font-size: 14.5px;
  margin-top: -10px;
  margin-bottom: 5px;
}
.theme-picker .clickable-theme-option {
  width: 115px;
  height: 70px;
  margin: 2px;
  top: -20px;
}
.theme-picker .clickable-theme-option iframe {
  pointer-events: none;
  position: relative;
  z-index: 0;
}
.theme-picker .create-theme {
  width: 100%;
  text-align: center;
  margin-top: 5px;
}
.theme-picker .create-theme a {
  text-decoration: none;
  cursor: default;
}
@media (-webkit-min-device-pixel-ratio: 2) {
  .theme-picker .theme-picker-x {
    margin: 12px;
  }
  .theme-picker .clickable-theme-option {
    top: -10px;
  }
}
